// Process incoming WhatsApp message
const message = $json.message || {};
const from = $json.from || 'unknown';
const text = message.text || '';

// Extract search query from message
let query = text.toLowerCase();

// Remove common greeting words
const greetings = ['hola', 'buenos dias', 'buenas tardes', 'buenas noches', 'hi', 'hello'];
greetings.forEach(greeting => {
  query = query.replace(new RegExp(`\\b${greeting}\\b`, 'gi'), '').trim();
});

// Remove question words
const questionWords = ['busco', 'necesito', 'quiero', 'donde esta', 'tienes', 'hay'];
questionWords.forEach(word => {
  query = query.replace(new RegExp(`\\b${word}\\b`, 'gi'), '').trim();
});

return {
  json: {
    original_message: text,
    processed_query: query,
    from: from,
    timestamp: new Date().toISOString()
  }
};

